fn menu() -> i8{
    let mut x: i8=0;

    while true{
        println!("==========");
        println!("MAIN MENU");
        println!("1) Play Game");
        println!("2) Save Game");
        println!("3) Load Game");
        println!("4) Exit");

        let mut input_line = String::new();
        match io::stdin().read_line(&mut input_line) {
            Ok(0) => {
                println!("EOF!");
                process::exit(1);
            }
            Ok(_) => {
                match input_line.trim().parse::<i8>() {
                    Ok(num) => x=num,
                    Err(e) => {println!("Invalid number"); continue;}
                };
            }
            Err(error) => {
                println!("Error reading line! {:}", error);
                process::exit(1);
            }
        }
        if (x>=1 && x<=4){
            break;
        }else{
            println!("Invalid option");
            continue;
        }

    }
    return x;
}

